   const showCartHandler = ()=>{
    setCartIsShown(true);
   }
   const hideCartHandler =()=>{
    setCartIsShown(false);
   }
  return (
    <Fragment>
      <Cart/>
     <Header/>
     <main>
      <Meals/>
     </main>
    </Fragment>
  );
}

export default App;
